import React from 'react';
import { NavButton } from './index';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome/index';
import {
  faAdjust,
  faAlignJustify,
  faCog,
  faFillDrip,
  faFont,
  faSignOutAlt,
  faShareAlt,
  faTextHeight,
  faTasks,
  faUser,
} from '@fortawesome/free-solid-svg-icons/index';
import WidgetContext from '../../context/widget-context';
import NavLogo from '../Logos/NavLogo';
import router from 'next/router';
import { doSignOut } from '../../firebase/auth';
import * as ROUTES from '../../constants/routes';

const icons = {
  colors: <FontAwesomeIcon icon={faFillDrip} />,
  lightDarkMode: <FontAwesomeIcon icon={faAdjust} />,
  font: <FontAwesomeIcon icon={faFont} />,
  fontSize: <FontAwesomeIcon icon={faTextHeight} />,
  share: <FontAwesomeIcon icon={faShareAlt} />,
  signOut: <FontAwesomeIcon icon={faSignOutAlt} />,
  text: <FontAwesomeIcon icon={faAlignJustify} />,
};

const NavDashboard = () => {
  return (
      <WidgetContext.Consumer>
        {context => (
            <nav
                className="flex items-center justify-center md:justify-between flex-wrap bg-bg px-1 py-4 md:py-6 sticky">
              <NavLogo />
              <div className="
        flex
        w-full
        flex-grow
        content-center
        justify-center
        md:w-1/2
        md:content-left
        md:justify-start
        tab">
                <div className="lg:flex-grow my-2">
                  <NavButton
                      icon={<FontAwesomeIcon icon={faTasks} />}
                      title="Jobs"
                      onClick={e => {
                        e.preventDefault();
                        context.openWidget('jobs');
                      }}
                  />
                  <NavButton
                      icon={<FontAwesomeIcon icon={faUser} />}
                      title="Account"
                      onClick={e => {
                        e.preventDefault();
                        context.openWidget('user');
                      }}
                  />
                  <NavButton
                      icon={<FontAwesomeIcon icon={faCog} />}
                      title="Settings"
                      onClick={e => {
                        e.preventDefault();
                        context.openWidget('settings');
                      }}
                  />
                </div>
                <div className="my-2">
                  <NavButton
                      icon={icons.signOut}
                      title="Sign out"
                      onClick={e => {
                        e.preventDefault();
                        doSignOut().then(() => router.push(ROUTES.SIGN_IN));
                      }}
                  />
                </div>
              </div>
            </nav>
        )}
      </WidgetContext.Consumer>
  );
};

export default NavDashboard ;
